mapper.select:
select no, subject, userid, date_format(write_date,'%y-%m-%d %H:%i') write_date, is_notice from board where is_notice=false
and subject like '%${searchWord}%'
order by is_notice desc, no desc limit ${onePageRecord} offset ${offsetIndex}

select b.no bno, b.userid buserid, b.subject bsubject, b.content bcontent, b.write_date, b.ip bip, r.no rno, r.userid ruserid, r.subject rsubject, r.content rcontent, r.write_date rwrite_date, r.ip rip from board b left outer join reply_board r on b.no = r.no where b.is_notice=false
and b.subject like '%${searchWord}%'
order by b.no desc limit ${onePageRecord} offset ${offsetIndex}
